# Parsear el HTML
soup = BeautifulSoup(html, 'html.parser')

# Inicializar una lista para almacenar las preguntas y respuestas
preguntas_y_respuestas = []

# Encontrar todas las divisiones de contenido con clase "content"
divs_content = soup.find_all('div', class_='content')

# Recorrer cada división de contenido
for div_content in divs_content:
    # Obtener el enunciado de la pregunta
    enunciado_pregunta = div_content.find('div', class_='qtext').text.strip()
    
    # Encontrar todas las opciones de respuesta
    opciones_respuesta = div_content.find_all('input', type='radio')
    
    # Inicializar una lista para almacenar las opciones de respuesta
    respuestas = []
    
    # Recorrer cada opción de respuesta
    for opcion in opciones_respuesta:
        texto_opcion = opcion.find_next_sibling('label')
    if texto_opcion is not None:
        texto_opcion = texto_opcion.text.strip()
        es_correcta = 'checked' in opcion.attrs
        respuestas.append({"texto": texto_opcion, "correcta": es_correcta})
    
    # Agregar la pregunta y respuestas a la lista
    preguntas_y_respuestas.append({"pregunta": enunciado_pregunta, "respuestas": respuestas})

# Formatear las preguntas y respuestas como una cadena de texto en formato JavaScript
codigo_js = "var preguntasYRespuestas = " + str(preguntas_y_respuestas) + ";"

# Guardar el código JavaScript en un archivo
with open("preguntas_y_respuestas.js", "w") as archivo_js:
    archivo_js.write(codigo_js)

print("El archivo JavaScript ha sido creado exitosamente.")
